import React, { Component, useState} from 'react';
import Button from "react-bootstrap/Button";
import Form from "react-bootstrap/Form";
import Modal from "react-bootstrap/Modal";
import "bootstrap/dist/css/bootstrap.min.css";
//import "./user-login.css";

const initialValues = {

};

function Login() {
const [credential, getCredential] = useState("");
const [password, getPassword] = useState("");
const [show, setShow] = useState(false);
const handleClose = () => setShow(false);
const handleShow = () => setShow(true);

function handleSubmit(event) {
event.preventDefault();
}

return (
<div className="userLoginModal">
<Button variant="primary" onClick={handleShow}>
        Login
</Button>

<Modal show={show} onHide={handleClose}>
    <Modal.Header closeButton>
        <Modal.Title>Login</Modal.Title>
    </Modal.Header>

    <Modal.Body>
    <div className="UserLogin">
        <h1> Welcome to FitnessTime </h1>
    <form onSubmit={handleSubmit}>
    <Form.Group controlId="Credential" bsSize="large">
        <Form.Label>Username or Email: </Form.Label>
        <Form.Control
        autoFocus
        type="text"
        value={credential}
        onChange={e => getCredential(e.target.value)}
        />
    </Form.Group>
    <Form.Group controlId="password" bsSize="large">
        <Form.Label>Password: </Form.Label>
        <Form.Control
        autoFocus
        type="password"
        value={password}
        onChange={e => getPassword(e.target.value)}
        />
    </Form.Group>
    </form>
    </div>
    </Modal.Body>

    <Modal.Footer>
        <Button block bsSize="large" validate={validate} type="submit">
            Login
        </Button>
    </Modal.Footer>
</Modal>
</div>
);
}

const  validateCredential = (field1) => {
    if(field.trim()  === '')
            return `${field} is required`;
    else if(/^[a-zA-Z0-9.!#$%&’*+/=?^_`{|}~-]+@[a-zA-Z0-9-]+(?:\.[a-zA-Z0-9-]+)*$/.test(field,)) //check if email
                //check database
                return null; //no error message if found in db

    return null;
};

const validatePassword = (field2) => {
    if(field.trim() === '')
        return `${field} is required`;
    else
        //check db
        return null;
};

const validate = {
    field1: validateCredential,
    field2: validatePassword,
};

export default Login;
